CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

START TRANSACTION;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20250531122404_FirstBase') THEN
    CREATE TABLE "CardBuffers" (
        "Id" integer GENERATED BY DEFAULT AS IDENTITY,
        "Name" text,
        "Patronymic" text,
        "Surname" text,
        "Num" text,
        "Room" text,
        date timestamp with time zone NOT NULL,
        CONSTRAINT "PK_CardBuffers" PRIMARY KEY ("Id")
    );
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20250531122404_FirstBase') THEN
    CREATE TABLE "Users" (
        "Id" integer GENERATED BY DEFAULT AS IDENTITY,
        "Name" text,
        "Patronymic" text,
        "Surname" text,
        "Email" text,
        "Password" text,
        "Role" text,
        "Num" text,
        CONSTRAINT "PK_Users" PRIMARY KEY ("Id")
    );
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20250531122404_FirstBase') THEN
    CREATE TABLE "TimeTracks" (
        "Id" integer GENERATED BY DEFAULT AS IDENTITY,
        "dateStamp" timestamp with time zone NOT NULL,
        status boolean NOT NULL,
        "Room" text,
        "UserId" integer NOT NULL,
        CONSTRAINT "PK_TimeTracks" PRIMARY KEY ("Id"),
        CONSTRAINT "FK_TimeTracks_Users_UserId" FOREIGN KEY ("UserId") REFERENCES "Users" ("Id") ON DELETE CASCADE
    );
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20250531122404_FirstBase') THEN
    CREATE INDEX "IX_TimeTracks_UserId" ON "TimeTracks" ("UserId");
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20250531122404_FirstBase') THEN
    INSERT INTO "__EFMigrationsHistory" ("MigrationId", "ProductVersion")
    VALUES ('20250531122404_FirstBase', '9.0.1');
    END IF;
END $EF$;
COMMIT;

